<?phpclass Controller {    protected $load;    protected $head;    public function __construct(){        $this->load = new Loader();        $this->load->library('head');        $this->head = new Head();    }    public function redirect($url, $wait = 0){        if ($wait == 0){            header("Location:$url");        } /*else {            include CURR_VIEW_PATH . "message.html";        }*/        exit;    }    public function url_origin( $s, $use_forwarded_host = false ) {        $ssl      = ( ! empty( $s['HTTPS'] ) && $s['HTTPS'] == 'on' );        $sp       = strtolower( $s['SERVER_PROTOCOL'] );        $protocol = substr( $sp, 0, strpos( $sp, '/' ) ) . ( ( $ssl ) ? 's' : '' );        $port     = $s['SERVER_PORT'];        $port     = ( ( ! $ssl && $port=='80' ) || ( $ssl && $port=='443' ) ) ? '' : ':'.$port;        $host     = ( $use_forwarded_host && isset( $s['HTTP_X_FORWARDED_HOST'] ) ) ? $s['HTTP_X_FORWARDED_HOST'] : ( isset( $s['HTTP_HOST'] ) ? $s['HTTP_HOST'] : null );        $host     = isset( $host ) ? $host : $s['SERVER_NAME'] . $port;        return $protocol . '://' . $host;    }    public function full_url( $s, $use_forwarded_host = false ) {        return $this->url_origin( $s, $use_forwarded_host ) . $s['REQUEST_URI'];    }}