<?phpclass Framework {    public static function run(){        self::init();        self::rewriteEngine();        self::autoload();        self::dispatch();    }    private static function init() {        include "system/config/init_config.php";    }    private static function autoload() {        spl_autoload_register(array(__CLASS__,'load'));    }    private static function load($classname)    {        if (substr($classname, -10) == "Controller") {            require_once CURR_CONTROLLER_PATH . "$classname.php";        } elseif (substr($classname, -5) == "Model") {            require_once MODEL_PATH . "$classname.php";        }    }    private static function dispatch() {        $controller_name = CONTROLLER . "Controller";        $action_name = ACTION . "Action";        $controller = new $controller_name;        $controller->$action_name();    }    private static function rewriteEngine(){        $path = ltrim($_SERVER['REQUEST_URI'], '/');    // Trim leading slash(es)        $elements = explode('/', $path);        echo '<pre>' . __FILE__ . ' : ' . __LINE__ . '<br>';        var_dump($elements);        die();    }}