<?phpclass Db {    protected $conn = false;    protected $sql = '';    public function __construct($sql) {        $this->sql = $sql;        $this->conn = new mysqli(DB_HOSTNAME, DB_USERNAME, DB_PASSWORD, DB_DATABASE);//        if (mysqli_connect_errno()){}    }    public function query($sql) {        return new Db($sql);    }    public function select($table, $cols = array()) {        $sql = "SELECT ";        if(empty($cols)) {            $sql .= '* ';        } else {            foreach($cols as $col){                $sql .= $col;                end($cols) === $col ? $sql .= ' ' : $sql .= ', ';            }        }        $sql .= 'FROM ' . $table;        return new Db($sql);    }    public function where($args = array()) {        $sql = $this->sql;        if($args){            $sql .= ' WHERE ';            foreach ($args as $arg){                $sql .= $arg;                end($args) === $arg ? $sql .= ' ' : $sql .= 'AND ';            }        }        return new Db($sql);    }    public function exec() {        $res = $this->conn->query($this->sql);        $return = [];        while($row = $res->fetch_assoc()){            $return[] = $row;        }        return $return;    }    public function create($data) {    }    public function __destruct() {        $this->conn->close();    }}