<?phpclass Db {    protected $conn = false;    protected $sql = '';    public function __construct($sql) {        $this->sql = $sql;        $this->conn = new mysqli(DB_HOSTNAME, DB_USERNAME, DB_PASSWORD, DB_DATABASE);//        if (mysqli_connect_errno()){}    }    public function select($cols = array(), $table) {        $sql = "SELECT ";        if(empty($cols)) {            $sql .= '* ';        } else {            foreach($cols as $col){                $sql .= $col;                end($cols) === $col ? $sql .= ' ' : $sql .= ', ';            }        }        $sql .= 'FROM ' . $table;        return new Db($sql);    }    public function where($args) {        $this->sql .= ' WHERE true';        return new Db($this->sql);    }    public function exec() {        $result = mysqli_query($this->conn,$this->sql);        $row = mysqli_fetch_assoc($result);        return $row;    }    public function create($data) {    }    public function __destruct() {        $this->conn->close();    }}